<node CREATED="1372791615910" ID="ID_1123247601" MODIFIED="1372791617056" TEXT="between two design contexts -- inside and outside of classroom "/>
<node CREATED="1372791595822" ID="ID_1340895317" MODIFIED="1372791607545" TEXT="building on facilitated nature"/>
</node>
<node CREATED="1372791635277" ID="ID_843289169" MODIFIED="1372863335349" TEXT="briefly explain how these settings differ in ">
<node CREATED="1372791690685" ID="ID_107686326" MODIFIED="1372791695639" TEXT="affordances "/>
<node CREATED="1372791696092" ID="ID_1892510148" MODIFIED="1372791707239" TEXT="possible target users "/>
<node CREATED="1372791707787" ID="ID_1623974902" MODIFIED="1372791742550" TEXT="available technology "/>
</node>
